];

// 各階級のランクマッピングを作成（数値に変換して比較しやすくする）
const rankOrder = {
  'Y': 5, // 横綱
  'O': 4, // 大関
  'S': 3, // 関脇
  'K': 2, // 小結
  'M': 1, // 前頭
  'J': 0, // 十両
  'Ms': -1, // 幕下
  'Sd': -2, // 三段目
  'Jd': -3  // 序二段
};

// 各記録のランク変化を追跡できるようにデータを加工
const processedData = data.map(record => {
  // ランクの最初の文字を取得（例：Y1e → Y）
  const rankType = record.RANK.charAt(0);

  // ランク数値に変換
  const rankValue = rankOrder[rankType] || -4; // 不明なランクは最低値を設定

  // 勝率を計算（PREV_WとPREV_Lが両方0の場合は欠場と見なす）
  const totalMatches = record.PREV_W + record.PREV_L;
  const winRate = totalMatches > 0 ? (record.PREV_W / totalMatches * 100).toFixed(1) : null;

  // 日付文字列を年と月に分割
  const [year, month] = record.BASHO.split('.');

  return {
    ...record,
    rankType,
    rankValue,
    winRate,
    year: parseInt(year),
    month: parseInt(month),
    totalMatches
  };
});

// 年ごとの平均勝率を計算
const yearlyWinRates = {};
processedData.forEach(record => {
  if (record.winRate !== null) {
    if (!yearlyWinRates[record.year]) {
      yearlyWinRates[record.year] = {
        totalWinRate: 0,
        count: 0
      };
    }
    yearlyWinRates[record.year].totalWinRate += parseFloat(record.winRate);
    yearlyWinRates[record.year].count += 1;
  }
});

// 平均を計算
const yearlyAvgWinRates = Object.keys(yearlyWinRates).map(year => {
  const { totalWinRate, count } = yearlyWinRates[year];
  return {
    year: parseInt(year),
    avgWinRate: (totalWinRate / count).toFixed(1)
  };
});

// ランク推移を抽出
const rankProgression = processedData.map(record => ({
  basho: record.BASHO,
  rank: record.RANK,
  rankValue: record.rankValue
}));

// 横綱、大関、関脇など、各階級での場所数をカウント
const rankCounts = {};
processedData.forEach(record => {
  if (!rankCounts[record.rankType]) {
    rankCounts[record.rankType] = 0;
  }
  rankCounts[record.rankType]++;
});

// 怪我や欠場期間を特定（PREV_WとPREV_Lが両方0の場所）
const absences = processedData.filter(record => 
  record.PREV_W === 0 && record.PREV_L === 0
).map(record => record.BASHO);

// 勝率70%以上の優秀な成績の場所を抽出
const excellentPerformances = processedData.filter(record => 
  record.winRate !== null && parseFloat(record.winRate) >= 70
).map(record => ({
  basho: record.BASHO,
  winRate: record.winRate,
  wins: record.PREV_W,
  losses: record.PREV_L
}));

// 年ごとの負傷や復帰のパターンを分析
// 大きなランク変動（上昇または下降）を特定
const rankChanges = [];
for (let i = 1; i < processedData.length; i++) {
  const prev = processedData[i-1];
  const curr = processedData[i];

  const change = curr.rankValue - prev.rankValue;

  // 大きなランク変化がある場合（2以上の上昇または下降）
  if (Math.abs(change) >= 2) {
    rankChanges.push({
      from: prev.BASHO,
      to: curr.BASHO,
      fromRank: prev.RANK,
      toRank: curr.RANK,
      change: change
    });
  }
}

// 勝利数と敗北数の比率変化を分析
const winLossRatio = processedData
  .filter(record => record.totalMatches > 0) // 欠場した場所を除外
  .map(record => ({
    basho: record.BASHO,
    ratio: record.PREV_W / (record.PREV_W + record.PREV_L)
  }));

// 分析結果を表示
console.log("照ノ富士の基本情報:");
console.log(`誕生日: ${data[0].BIRTH_DATE}`);
console.log(`キャリアの長さ: ${data.length}場所`);

console.log("\n階級別の場所数:");
Object.keys(rankCounts).sort((a, b) => rankOrder[b] - rankOrder[a]).forEach(rank => {
  const rankName = {
    'Y': '横綱', 'O': '大関', 'S': '関脇', 'K': '小結', 'M': '前頭',
    'J': '十両', 'Ms': '幕下', 'Sd': '三段目', 'Jd': '序二段'
  }[rank] || rank;

  console.log(`${rankName}: ${rankCounts[rank]}場所`);
});

console.log("\n年間平均勝率:");
yearlyAvgWinRates.forEach(item => {
  console.log(`${item.year}年: ${item.avgWinRate}%`);
});

console.log("\n優秀な成績（勝率70%以上）:");
excellentPerformances.forEach(item => {
  console.log(`${item.basho}: ${item.winRate}% (${item.wins}勝${item.losses}敗)`);
});

console.log("\n大きなランク変動:");
rankChanges.forEach(change => {
  const direction = change.change > 0 ? "上昇" : "下降";
  console.log(`${change.from}〜${change.to}: ${change.fromRank}から${change.toRank}へ${direction}`);
});

console.log("\n欠場した場所:");
absences.forEach(basho => {
  console.log(basho);
});

// キャリアにおける重要なマイルストーン
console.log("\n主要なマイルストーン:");
const milestones = [
  { basho: "2015.07", event: "大関に昇進" },
  { basho: "2017.11", event: "怪我により大関から陥落" },
  { basho: "2019.03", event: "最下位の序二段まで降下" },
  { basho: "2020.07", event: "幕内に復帰" },
  { basho: "2021.05", event: "再度大関に昇進" },
  { basho: "2021.09", event: "横綱に昇進" }
];

milestones.forEach(item => {
  console.log(`${item.basho}: ${item.event}`);
});

// 視覚化のためのデータを準備
const visualizationData = {
  rankProgression: rankProgression,
  winRatesByYear: yearlyAvgWinRates,
  excellentPerformances: excellentPerformances,
  absences: absences,
  milestones: milestones,
  rankCounts: Object.keys(rankCounts).map(rank => ({
    rank: rank,
    count: rankCounts[rank],
    name: {
      'Y': '横綱', 'O': '大関', 'S': '関脇', 'K': '小結', 'M': '前頭',
      'J': '十両', 'Ms': '幕下', 'Sd': '三段目', 'Jd': '序二段'
    }[rank] || rank
  }))
};

